mapper.select:
SELECT r.rcpno, r.uno, r.name, r.intro, r.c_time, r.ptn, r.rcp_dt, r.hits, r.rgst, r.gpa, u.uno, u.name userName, l.rcpno, l.lc_dt, l.uno likeUser FROM users u INNER JOIN rcps r on u.uno=r.uno LEFT OUTER JOIN rcp_lik l ON r.rcpno=l.rcpno and l.uno=#{userNo}
WHERE r.name LIKE '%${search.searchKeyword}%'
ORDER BY r.rcp_dt desc
limit #{startIndex}, #{len}

SELECT count(*)
FROM users u INNER JOIN rcps r on u.uno=r.uno
     LEFT OUTER JOIN rcp_lik l ON r.rcpno=l.rcpno and l.uno=#{userNo}      

  WHERE r.name LIKE '%${search.searchKeyword}%'


  ORDER BY r.rcp_dt desc

limit #{startIndex}, #{len}

mapper.insert:
		 INSERT INTO rcps(uno, name, intro, c_time, ptn, rcp_pd, rpimg, rcp_dt, rgst) 
		 VALUES(#{user.userNo}, #{recipe.recipeName}, #{recipe.cookTime}, #{recipe.intro}, #{recipe.portion}, 'rcp_pd', 'rpimg', now(), 1)
		 
            SELECT LAST_INSERT_ID();

mapper.update:
UPDATE rcps 
SET rcp_pd=#{recipeProduceDatas}, rpimg=#{recipeRepresentImages}
WHERE rcpno=#{recipeNo};

mapper.select:
SELECT r.rcpno, r.uno, r.name, r.intro, r.c_time, r.ptn, r.rcp_dt, r.hits,
 r.rgst, r.gpa, u.uno, u.name userName, l.rcpno, l.lc_dt, l.uno likeUser
FROM (users u INNER JOIN rcps r on u.uno=r.uno) 
LEFT OUTER JOIN rcp_lik l ON r.rcpno=l.rcpno and l.uno=${userNo}
ORDER BY r.rcp_dt desc
limit #{len}

SELECT r.rcpno, r.uno, r.name, r.intro, r.c_time, r.ptn, r.rcp_dt, r.hits, r.rgst, r.gpa, u.uno, u.name userName
FROM rcps r, users u
WHERE r.uno = u.uno and r.rcpno=#{value};
