mapper.select:
select
u.uno, u.name, u.email, u.pwd, u.img, u.intro, u.role, u.join_dt,
u.rcp_url, count(*) rcp_cnt
from users u inner join rcps r on u.uno =
r.uno
where u.uno = #{no}

mapper.update:
		UPDATE users

		  pwd = #{password},
      intro = #{intro},
			img = #{image}

		WHERE uno = #{userNo}
